htmx.defineExtension('ajax-header', {
    onEvent: function (name, evt) {
        if (name === "htmx:configRequest") {
            evt.detail.headers['X-Requested-With'] = 'XMLHttpRequest';
        }
    }
});

const deepClone = (obj: any) => {
	if (obj === null) return null;
  let clone = { ...obj };

  Object.keys(clone).forEach(
	  (key) =>
      (clone[key] = typeof obj[key] === "object" ? deepClone(obj[key]) : obj[key])
   );
	 return Array.isArray(obj) && obj.length
	   ? (clone.length = obj.length) && Array.from(clone)
	   : Array.isArray(obj)
     ? Array.from(obj)
     : clone;
};

{% if messages %}
    <ul class="messages">
        {% with messages|last as last_message %}
            <li{% if last_message.tags %} class="{{ last_message.tags }}"{% endif %}>{{ last_message }}</li>
        {% endwith %}
    </ul>
{% endif %}

{% if signup_form.email.errors %}
  {% for error in signup_form.email.errors %}
    <span class="[ input-message ] [ text-red-600 text-xs ]"
          data-status="invalid"
          data-view="public"
          id="invalid-signupmatch-msg">
        {{ error }}
    </span>
  {% endfor %}
{% endif %}

